contracts/BuyNowPayLater.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "./ComposooorRegister.sol";
import "./FakeKairos.sol";
import "./FlashLender.sol";
import "./IFlashBorrower.sol";
import "./MarketPlaceComposooored.sol";

contract BuyNowPayLater is IFlashBorrower, MarketPlaceComposooored {
    FakeKairos private kairos;
    FlashLender private flashLender;
    MarketPlace private market;
    IERC721 private nft;
    IERC20 private wEth; 

    constructor(
        FakeKairos _kairos,
        FlashLender _flashLender,
        MarketPlace _market,
        IERC721 _nft,
        IERC20 _wEth
    ) MarketPlaceComposooored(_market) {
        kairos = _kairos;
        flashLender = _flashLender;
        market = _market;
        nft = _nft;
        wEth = _wEth;
    }

    function buyNowPayLater() external {
        flashLender.flashBorrow(1 ether, abi.encode(msg.sender, nft, 1));
    }

    function flashCallback(bytes memory callbackData) external {
        (address initialMsgSender, IERC721 implem, uint tokenId) = abi.decode(callbackData, (address, IERC721, uint));
        wEth.approve(address(market), 1 ether);
        marketPlaceBuy(implem, tokenId);
        nft.approve(address(kairos), 1);
        kairos.borrow(implem, tokenId, 7 ether / 10);
        wEth.transferFrom(initialMsgSender, address(this), 3 ether / 10);
        wEth.approve(address(flashLender), 1 ether);
    }
}

contracts/ComposooorRegister.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

error MissingOffchainDataError(address registryAddress, string url, bytes abiArgs);

/// @title Composooor Parameter Register
/// @author Kairos Loan (@kairos-loan)
/// @notice as a user, use this contract to record offchain-computed parameters
///     as a contract, use it to consume those parameters
/// @dev to create your adapter, inherit from this contract
contract ComposooorRegister {
    // possible future improvement : support multiple data objects for the same tx

    mapping(address => mapping(uint => bytes)) private data;
    mapping(address => uint) private nonce; // number given is considered used

    /// @notice record a parameter for a one-time usage
    function recordParameter(bytes calldata _data) external {
        data[tx.origin][nonce[tx.origin] + 1] = _data;
    }

    /// @notice as a contract, consume a pre-computed parameter,
    ///     successive calls will give different parameters (computed for context)
    /// @dev correctness of the data MUST be checked
    function consumeParameter(string memory url, bytes memory abiArgs) internal returns (bytes memory data_) {
        data_ = data[tx.origin][nonce[tx.origin] + 1];
        if (data_.length == 0) {
            revert MissingOffchainDataError(address(this), url, abiArgs);
        }
        nonce[tx.origin]++;
    }
}

