contracts/MarketPlace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

struct SaleOffer {
    IERC721 implem;
    uint tokenId;
    uint price;
}

/// @notice NFT marketplace
contract MarketPlace {
    IERC20 private immutable wEth;

    constructor(IERC20 _wEth) {
        wEth = _wEth;
    }

    /// @notice buy a NFT from an offer to sell, signed by seller
    function buy(SaleOffer memory offer, bytes memory signature) external {
        address seller = ECDSA.recover(keccak256(abi.encode(offer)), signature);
        wEth.transferFrom(msg.sender, seller, offer.price);
        offer.implem.transferFrom(seller, msg.sender, offer.tokenId);
    }
}

contracts/MarketPlaceComposooored.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.18;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

import "./ComposooorRegister.sol";
import "./MarketPlace.sol";

contract MarketPlaceComposooored is ComposooorRegister {
    MarketPlace private immutable marketplace;

    constructor(MarketPlace _marketplace){
        marketplace = _marketplace;
    }

    function marketPlaceBuy(IERC721 implem, uint tokenId) internal {
        bytes memory data = consumeParameter("http://marketplace.api.composooor.com", abi.encode(implem, tokenId));
        (SaleOffer memory saleOffer, bytes memory signature) = abi.decode(data, (SaleOffer, bytes));
        marketplace.buy(saleOffer, signature);
    }
}

